import React, { useState, useEffect } from 'react';
import { Container, Box, Typography, ListItem, List, Link, IconButton } from '@mui/material';
import pfp from "../assets/pfp.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTwitter } from '@fortawesome/free-brands-svg-icons';
import { faLinkedin } from '@fortawesome/free-brands-svg-icons';

const LandingPage = () => {

  const projects = [
    { name: "Advocate AI - Legal Analysis Tool Kit", url: "https://www.advocateai.xyz/" },
    // Add more projects here
  ];

  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const aboutStyle = {
    display: 'flex',
    flexDirection: isMobile ? 'column' : 'row',
    minHeight: '100vh'
  };

  const sectionStyle = {
    width: isMobile ? '100%' : '50%',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  };

  return (
    <div>
      {/* Full-height section with background */}
      <Box
        style={{
          minHeight: '100vh',
          background: '#EEEEEE',
          display: 'flex',
          justifyContent: 'center', // Center the Container horizontally
        }}
      >
        <Container maxWidth="lg" style={{ display: 'flex', alignItems: 'center' }}>
          {/* Text and content inside the Container */}
          <Box style={{ marginLeft: '5%', width: '100%', marginTop: isMobile ? '-35%' : '-10%' }}>
            {
              isMobile ?
                <Typography variant="h1" style={{ fontWeight: 'bold', fontSize: '15vw' }}>
                  currently building
                </Typography>
                :
                <Typography variant="h1" style={{ fontWeight: 'bold'}}>
                  currently building
                </Typography>
            }
            {
              isMobile ?
                <Typography variant="h1" style={{ fontWeight: 'bold', fontSize: '15vw' }}>
                  (hopefully) cool things
                </Typography>
                :
                <Typography variant="h1" style={{ fontWeight: 'bold'}}>
                  (hopefully) cool things
                </Typography>
            }
            <Typography variant="h5" color="textSecondary" style={{ fontWeight: 'bold', marginTop: '2%' }}>
              - Jack Retterer
            </Typography>
          </Box>

          {/* Scroll Down Indicator */}
          <Typography
            style={{
              position: 'absolute',
              bottom: '1%',
              right: '2%',
              writingMode: 'vertical-rl',
              transform: 'rotate(0deg)',
              cursor: 'pointer',
            }}
            color="textSecondary"
            variant="body1"
            onClick={() => window.scrollTo(0, window.innerHeight)}
          >
            scroll down --->
          </Typography>
        </Container>
      </Box>

      <Box id="about" style={aboutStyle}>
        <Box style={sectionStyle}>
          <Box style={{ width: '85%' }}>
