import React from 'react';
import { Container, Box, Typography, Link as MuiLink } from '@mui/material';
import Experience from './Experience';
import Projects from './Projects';
import About from './About';

const LandingPage = () => {
  return (
    <div>
      {/* Full-height section with background */}
      <Box
        style={{
          minHeight: '100vh',
          background: '#EEEEEE',
          display: 'flex',
          justifyContent: 'center', // Center the Container horizontally
        }}
      >
        <Container maxWidth="lg" style={{ display: 'flex', alignItems: 'center' }}>
          {/* Text and content inside the Container */}
          <Box style={{ marginLeft: '5%', width: '100%' }}>
            <Typography variant="h5" color="textSecondary">
              Jack Retterer -
            </Typography>
            <Typography variant="h1" style={{ fontWeight: 'bold' }}>
              currently building
            </Typography>
            <Typography variant="h1" style={{ fontWeight: 'bold' }}>
              (hopefully) cool things.
            </Typography>
          </Box>

          {/* Scroll Down Indicator */}
          <Typography
            style={{
              position: 'absolute',
              bottom: 20,
              right: 20,
              writingMode: 'vertical-rl',
              transform: 'rotate(0deg)',
              cursor: 'pointer',
            }}
            color="textSecondary"
            variant="body1"
            onClick={() => window.scrollTo(0, window.innerHeight)}
          >
            scroll down --->
          </Typography>
        </Container>
      </Box>

      <Box style={{ display: 'flex', minHeight: '100vh' }}>
      {/* Image on the left */}
      <Box style={{ width: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Box style={{
          position: 'absolute',
          backgroundColor: '#EEEEEE',
          width: '30%', 
          height: '60%', 
          zIndex: -1,
          margin: '10% 0 0 -10%' // Negative margin to extend 10% past the image
        }} />
        <img src={pfp} alt="Profile" style={{ maxWidth: '100%', height: 'auto' }} />
      </Box>

      {/* Text on the right */}
      <Box style={{ width: '50%', display: 'flex', flexDirection: 'column', justifyContent: 'center', padding: '20px' }}>
        <Typography variant="h3" style={{ marginBottom: '20px' }}>
          About Me
        </Typography>
        <Typography variant="body1" paragraph>
          With a passion deeply rooted in the innovative realms of AI, crypto, and space, my journey has been nothing short of exciting. As someone who thrives on challenges, the ever-evolving landscapes of these fields have always captivated my curiosity and driven my ambitions.
        </Typography>
        <Typography variant="body1" paragraph>
          Building solutions that leverage the intricate possibilities of artificial intelligence, exploring the transformative potential of cryptocurrencies, and contributing to groundbreaking advancements in space technology – these are the avenues where I find my creativity and skills best expressed. Each project is a new adventure, a chance to push boundaries and redefine what's possible.
        </Typography>
      </Box>
    </Box>


      <Container maxWidth="lg">
        {/* About Section */}
        <Box id="about" style={{ minHeight: '100vh' }}>
          <About />
        </Box>

        {/* Experience Section */}
        <Box id="experience" style={{ minHeight: '100vh' }}>
          <Experience />
        </Box>

        {/* Projects Section */}
        <Box id="projects" style={{ minHeight: '100vh' }}>
          <Projects />
        </Box>
      </Container>
    </div>
  );
};

export default LandingPage;
