import React from 'react';
import { Container, Box, Typography, Link as MuiLink } from '@mui/material';
import Experience from './Experience';
import Projects from './Projects';
import About from './About';

const LandingPage = () => {
  return (
    <div>
      <Container maxWidth="lg">
        {/* Full-height section with text */}
        <Box
          style={{
            minHeight: '100vh',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'flex-start',
            background: '#EEEEEE'
          }}
        >
          {/* Updated text alignment and content */}
          <Box style={{ marginLeft: '5%' }}>
            <Typography variant="body1" color="textSecondary">
              Jack Retterer - 
            </Typography>
            <Typography variant="h1" style={{ fontWeight: 'bold' }}>
              currently building,
            </Typography>
            <Typography variant="h1" style={{ fontWeight: 'bold' }}>
              hopefully cool things.
            </Typography>
          </Box>

          {/* Scroll Down Indicator */}
          <Typography
            style={{
              position: 'absolute',
              bottom: 20,
              right: 20,
              writingMode: 'vertical-rl',
              transform: 'rotate(0deg)', // Rotate text to flow downwards with arrow pointing to bottom
              cursor: 'pointer',
            }}
            color="textSecondary"
            variant="body1"
            onClick={() => window.scrollTo(0, window.innerHeight)} // Scroll to next section on click
          >
            scroll down --->
          </Typography>

        </Box>

        {/* About Section */}
        <Box id="about" style={{ minHeight: '100vh' }}>
          <About />
        </Box>

        {/* Experience Section */}
        <Box id="experience" style={{ minHeight: '100vh' }}>
          <Experience />
        </Box>

        {/* Projects Section */}
        <Box id="projects" style={{ minHeight: '100vh' }}>
          <Projects />
        </Box>
      </Container>
    </div>
  );
};

export default LandingPage;
