import React, { useState, useEffect } from 'react';
import { Container, Box, Typography, ListItem, List, Link, IconButton, Grid, Chip, Paper } from '@mui/material';
import { styled } from '@mui/system';
import pfp from "../assets/pfp.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTwitter } from '@fortawesome/free-brands-svg-icons';
import { faLinkedin, faGithub } from '@fortawesome/free-brands-svg-icons';
import ComputerIcon from '@mui/icons-material/Computer';
import StorageIcon from '@mui/icons-material/Storage';
import PsychologyIcon from '@mui/icons-material/Psychology';

const ProjectContainer = styled(Box)(({ theme }) => ({
  backgroundColor: "#FFF",
  padding: theme.spacing(6, 10),
  [theme.breakpoints.down('sm')]: {
    padding: theme.spacing(4, 2),
  },
}));

const ProjectTitle = styled(Typography)({
  fontWeight: 'bold',
  marginBottom: '1rem',
});

const ProjectLink = styled(Link)({
  color: '#0077b5',
  textDecoration: 'none',
  '&:hover': {
    textDecoration: 'underline',
  },
});

const TechChip = styled(Chip)(({ theme }) => ({
  margin: theme.spacing(0.5),
}));

const LandingPage = () => {
  const classes = useStyles();

  const projects = [
    { name: "Advocate AI - Legal Analysis Tool Kit", url: "https://www.advocateai.xyz/" },
    { name: "Pen2Pixel - In Development", url: "https://www.google.com"}
