import * as React from 'react';
import { Box, Button, Container, Grid, Typography, Link, IconButton, Card, CardContent, CardActions } from '@mui/material';

const Blog = () => {
    // Sample data for blog posts
    const blogPosts = [
        { title: "Blog Post 1", summary: "Summary of the first blog post...", date: "2023-01-01" },
        { title: "Blog Post 2", summary: "Summary of the second blog post...", date: "2023-01-08" },
        // Add more blog posts here
    ];

    return (
        <Box maxWidth="lg" sx={{ py: 8, backgroundColor: '#EEEEEE'}}>
            <Typography variant="h4" sx={{ mb: 4, fontWeight: 'bold', color: '#333' }}>
                Every Sunday I write a blog on what is on my mind.
            </Typography>
            <Grid container spacing={4}>
                {blogPosts.map((post, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index}>
                        <Card variant="outlined" sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                            <CardContent sx={{ flexGrow: 1 }}>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {post.title}
                                </Typography>
                                <Typography>
                                    {post.summary}
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <Button size="small">Read More</Button>
                                <Typography variant="body2" color="text.secondary">
                                    {post.date}
                                </Typography>
                            </CardActions>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Box>
    );
}

export default Blog;
