import React, { useState, useEffect } from 'react';
import { Container, Box, Typography, ListItem, List, Link, IconButton, Grid, Chip, Paper, useTheme } from '@mui/material';
import { styled } from '@mui/system';
import pfp from "../assets/pfp.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTwitter } from '@fortawesome/free-brands-svg-icons';
import { faLinkedin, faGithub } from '@fortawesome/free-brands-svg-icons';
import ComputerIcon from '@mui/icons-material/Computer';
import StorageIcon from '@mui/icons-material/Storage';
import PsychologyIcon from '@mui/icons-material/Psychology';

const ProjectContainer = styled(Box)(({ theme }) => ({
  backgroundColor: "#FFF",
  padding: theme.spacing(6, 10),
  [theme.breakpoints.down('sm')]: {
    padding: theme.spacing(4, 2),
  },
}));

const ProjectTitle = styled(Typography)({
  fontWeight: 'bold',
  marginBottom: '1rem',
});

const ProjectLink = styled(Link)({
  color: '#0077b5',
  textDecoration: 'none',
  '&:hover': {
    textDecoration: 'underline',
  },
});

const TechChip = styled(Chip)(({ theme }) => ({
  margin: theme.spacing(0.5),
}));

const LandingPage = () => {

  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const aboutStyle = {
    display: 'flex',
    flexDirection: isMobile ? 'column' : 'row',
    minHeight: '100vh'
  };

  const sectionStyle = {
    width: isMobile ? '100%' : '50%',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  };

  const theme = useTheme();

  return (
    <div>
      {/* Full-height section with background */}
      <Box
        style={{
          minHeight: '100vh',
          background: '#EEEEEE',
          display: 'flex',
          justifyContent: 'center', // Center the Container horizontally
        }}
      >
        <Container maxWidth="lg" style={{ display: 'flex', alignItems: 'center' }}>
          {/* Text and content inside the Container */}
          <Box style={{ marginLeft: '5%', width: '100%', marginTop: isMobile ? '-35%' : '-10%' }}>
            {
              isMobile ?
                <Typography variant="h1" style={{ fontWeight: 'bold', fontSize: '15vw' }}>
                  currently building
                </Typography>
                :
                <Typography variant="h1" style={{ fontWeight: 'bold'}}>
                  currently building
                </Typography>
            }
            {
              isMobile ?
                <Typography variant="h1" style={{ fontWeight: 'bold', fontSize: '15vw' }}>
                  cool things.
                </Typography>
                :
                <Typography variant="h1" style={{ fontWeight: 'bold'}}>
                  cool things.
                </Typography>
            }
          </Box>

          {/* Scroll Down Indicator */}
          <Typography
            style={{
              position: 'absolute',
              bottom: '1%',
              right: '2%',
              writingMode: 'vertical-rl',
